import { monitorEventLoopDelay } from 'perf_hooks';

<template>
  <header>
    <h1 class="d-flex justify-content-center">My movies - zadanie rekrutacyjne</h1>
    <h5 class="d-flex justify-content-center">Aleksander Jóźwiak</h5>
  </header>
  <div>
    <table class="table table-striped">
        <thead>
            <tr>
                <th>
                    Id
                </th>
                <th>
                    Title
                </th>
                <th>
                    Director
                </th>
                <th>
                    Year
                </th>
                <th>
                    Rate
                </th>
                <th>
                    <button id="addBtn" class="btn">
                        Dodaj 
                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-plus-circle" viewBox="0 0 16 16">
                        <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14zm0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16z"/>
                        <path d="M8 4a.5.5 0 0 1 .5.5v3h3a.5.5 0 0 1 0 1h-3v3a.5.5 0 0 1-1 0v-3h-3a.5.5 0 0 1 0-1h3v-3A.5.5 0 0 1 8 4z"/>
                    </svg>
                    </button> 
                </th>
            </tr>
        </thead>
        <tbody>
            <tr v-for="(mov, index) in movies" v-bind:key="index">
                <td>{{mov.id}}</td>
                <td>{{mov.title}}</td>
                <td>{{mov.director}}</td>
                <td>{{mov.year}}</td>
                <td>{{mov.rate}}</td>
                <td>
                    <button type="button" class="btn btn-light mr-1">
                        <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-pencil-square" viewBox="0 0 16 16">
                            <path d="M15.502 1.94a.5.5 0 0 1 0 .706L14.459 3.69l-2-2L13.502.646a.5.5 0 0 1 .707 0l1.293 1.293zm-1.75 2.456-2-2L4.939 9.21a.5.5 0 0 0-.121.196l-.805 2.414a.25.25 0 0 0 .316.316l2.414-.805a.5.5 0 0 0 .196-.12l6.813-6.814z"/>
                            <path fill-rule="evenodd" d="M1 13.5A1.5 1.5 0 0 0 2.5 15h11a1.5 1.5 0 0 0 1.5-1.5v-6a.5.5 0 0 0-1 0v6a.5.5 0 0 1-.5.5h-11a.5.5 0 0 1-.5-.5v-11a.5.5 0 0 1 .5-.5H9a.5.5 0 0 0 0-1H2.5A1.5 1.5 0 0 0 1 2.5v11z"/>
                        </svg>
                    </button>
                    <button type="button" class="btn btn-light mr-1">
                        <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-trash3" viewBox="0 0 16 16">
                            <path d="M6.5 1h3a.5.5 0 0 1 .5.5v1H6v-1a.5.5 0 0 1 .5-.5ZM11 2.5v-1A1.5 1.5 0 0 0 9.5 0h-3A1.5 1.5 0 0 0 5 1.5v1H2.506a.58.58 0 0 0-.01 0H1.5a.5.5 0 0 0 0 1h.538l.853 10.66A2 2 0 0 0 4.885 16h6.23a2 2 0 0 0 1.994-1.84l.853-10.66h.538a.5.5 0 0 0 0-1h-.995a.59.59 0 0 0-.01 0H11Zm1.958 1-.846 10.58a1 1 0 0 1-.997.92h-6.23a1 1 0 0 1-.997-.92L3.042 3.5h9.916Zm-7.487 1a.5.5 0 0 1 .528.47l.5 8.5a.5.5 0 0 1-.998.06L5 5.03a.5.5 0 0 1 .47-.53Zm5.058 0a.5.5 0 0 1 .47.53l-.5 8.5a.5.5 0 1 1-.998-.06l.5-8.5a.5.5 0 0 1 .528-.47ZM8 4.5a.5.5 0 0 1 .5.5v8.5a.5.5 0 0 1-1 0V5a.5.5 0 0 1 .5-.5Z"/>
                        </svg>
                    </button>
                </td>
            </tr>
        </tbody>
        </table>
  </div>

</template>


<script>
import axios from 'axios'

export default {
    name: 'MovieList',
data(){
    return {
        movies:[]
    }
},
methods:{
    refreshData(){
        axios.get("https://localhost:7090/data").then((response)=>{
            this.movies = response.data;
        });
        //let get = axios.get("https://localhost:7090/data");
        //this.movies = get.data;
    }
},
mounted:function(){
    this.refreshData();
}
}
</script>

<!-- Add "scoped" attribute to limit CSS to this component only -->
<style scoped>
#addBtn{
    display: flex;
    flex-direction: row;
    justify-content: space-around;
    align-items: center;
    border: none;
    width: 85%;
    height: 85%;
    border-radius: 5%;
    width: 32px;
    height: 32px;
}
.btn {
    background-color: #4ec1de;
    transition-duration: 0.3s;
}
.btn:hover {
    background-color: gold;
}
header {
    width: 100%;
    height: 150px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-color: #4ec1de;
}
table, tbody, thead {
    width: 100%;
}
th {
    align-items: center;
}
tr, th {
    display: flex;
    justify-content: center;
    width: 100%;
    height: 60px;
}
td {
    width: 16.7%;
}
</style>
